import './update.css'
import axios from 'axios';
import React from 'react';
import { Routes, Route, Link} from 'react-router-dom';
import { useState, useEffect } from 'react';
import Card from './card';
import Updates from './Updates';



function Update(){

  const [users, setUsers] = useState([]);
    useEffect(() => {
        getUsers();
    }, []);

    function getUsers() {
        axios.get('https://database.resultpahle.com/users/').then(function(response) {
            console.log(response.data);
            setUsers(response.data);
        });
    }


    return(
        <div className='update'>
            <div className='Top'>
            <span>Find The Right<br/> Result</span>
            </div>
            <div className='Mid'>
                <span className='Heading'>New Updates</span>
                {users.map((d, i) =>  (
            <ul key={i} className='data'>
              
              <li><Link to={`user/${d.id}/edit`} style={{marginRight: "10px"}}>{d.email}</Link></li>
             
            </ul>
            
          ))}
        
                <Link to="user/list">View more</Link>
          <Routes>
        
          <Route path="/user" element={<Updates />} />
        </Routes>

            </div>
            <div className='card1'>
            <Card></Card>
            </div>
            <div className='card2'>
            <Card></Card>
            </div>
            <div className='card3'>
            <Card></Card>
            </div>
        </div>
       
)}
export default Update
