    //         console.log(response.data);
    //         navigate('/');
    //     });
        
    // }
    return (
        <div>
            <h1>Edit user</h1>
    
                <table cellSpacing="10">
                    <tbody>
                        <tr>
                            <th>
                                <label>Email: </label>
                            </th>
                            <td> 
                                {inputs.email}
                            </td>
                        </tr>
                        
                        
                    </tbody>
                </table>
        
        </div>
    )
}
